package com.example.serendipitydonationapp.cloth.indiaCloth;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import com.example.serendipitydonationapp.R;
import com.example.serendipitydonationapp.cloth.uaeCloth.Cloth_org;
import com.example.serendipitydonationapp.cloth.usaCloth.UsaCloth_rvAdapter;

import java.util.ArrayList;

public class IndiaClothActivity extends AppCompatActivity {

    private RecyclerView cloth_rv;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_india_cloth);

        cloth_rv = findViewById(R.id.cloth_rv);

        ArrayList<Cloth_org> cloth_org = new ArrayList<>();
        cloth_org.add(new Cloth_org("Clothes Box Foundation",
                "https://images.yourstory.com/cs/images/companies/b1e64c4a44ca-Logocbfpng-1617184707157.jpg?fm=auto&ar=1:1&mode=fill&fill=solid&fill-color=fff",
                "https://www.clothesboxfoundation.org/requestapickup.html"));

        cloth_org.add(new Cloth_org("Share At Door Step",
                "https://pbs.twimg.com/profile_images/755020901725245440/PW7-J3qF_400x400.jpg",
                "https://sadsindia.org/#donate"));

        cloth_org.add(new Cloth_org("Apang Manav Mandal",
                "https://targetstudy.com/files/img/2/18934/L_52870.gif",
                "http://amm-india.org/"));

        cloth_org.add(new Cloth_org("Mahesh Foundation",
                "https://maheshfoundation.org/wp-content/uploads/2020/09/cropped-MF-PNG.png",
                "https://maheshfoundation.org/"));

        cloth_org.add(new Cloth_org("Happiee Souls",
                "https://pbs.twimg.com/profile_images/795565350926487552/ECrEaIhN_400x400.jpg",
                "https://happieesouls.com/ngolisting/category/index/ngo/happieesouls/?ngo_btn/bucket-list-page"));

        cloth_org.add(new Cloth_org("Clothes for Help",
                "data:image/png;base64,",
                "https://responsiblecharity.org/pages/clothesforhelp"));

        IndiaCloth_rvAdapter adapter = new IndiaCloth_rvAdapter(this);
        adapter.setCloth_org(cloth_org);

        cloth_rv.setAdapter(adapter);
        cloth_rv.setLayoutManager(new GridLayoutManager(this, 2));
    }
}