package com.example.serendipitydonationapp.money.cancer.indiaCancer;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import com.example.serendipitydonationapp.R;
import com.example.serendipitydonationapp.money.cancer.uaeCancer.Cancer_org;
import com.example.serendipitydonationapp.money.cancer.usaCancer.UsaCancer_rvAdapter;

import java.util.ArrayList;

public class IndiaCancerActivity extends AppCompatActivity {

    private RecyclerView cancer_rv;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_india_cancer);

        cancer_rv = findViewById(R.id.cancer_rv);

        ArrayList<Cancer_org> cancer_org = new ArrayList<>();
        cancer_org.add(new Cancer_org("Indian Cancer Society",
                "https://www.indiancancersociety.org/images/logo-intro.png",
                "https://www.indiancancersociety.org/how-you-can-help/donate.aspx"));

        cancer_org.add(new Cancer_org("Tata Memorial Centre",
                "https://upload.wikimedia.org/wikipedia/en/thumb/0/05/Tata_Memorial_Hospital_Logo.svg/1200px-Tata_Memorial_Hospital_Logo.svg.png",
                "https://tmc.gov.in/m_donation_new"));

        cancer_org.add(new Cancer_org("HelpAge India",
                "https://images-eu.ssl-images-amazon.com/images/G/31/img19/AmazonPay/LPA/Donation/Kundan/HelpAge._CB432424764_.jpg",
                "https://www.helpageindia.org/cancer-donate/"));

        cancer_org.add(new Cancer_org("Give India",
                "https://cdn.givind.org/static/images/giveindia-logo-v2.jpg",
                "https://www.giveindia.org/cancer-care"));

        cancer_org.add(new Cancer_org("Cope with Cancer",
                "data:image/png;base64,",
                "https://www.copewithcancer.org/financial-aid-at-tata-memorial"));

        cancer_org.add(new Cancer_org("Cancer Foundation of India",
                "https://www.unitedwaymumbai.org/ngo_logos/552_logo.png",
                "http://www.cancerfoundationofindia.org/donate.html"));

        IndiaCancer_rvAdapter adapter = new IndiaCancer_rvAdapter(this);
        adapter.setCancer_org(cancer_org);

        cancer_rv.setAdapter(adapter);
        cancer_rv.setLayoutManager(new GridLayoutManager(this, 2));
    }
}