package com.example.serendipitydonationapp.money.pod.indiaPod;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import com.example.serendipitydonationapp.R;
import com.example.serendipitydonationapp.money.pod.uaePod.Pod_org;
import com.example.serendipitydonationapp.money.pod.usaPod.UsaPod_rvAdapter;

import java.util.ArrayList;

public class IndiaPodActivity extends AppCompatActivity {

    private RecyclerView pod_rv;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_india_pod);

        pod_rv = findViewById(R.id.pod_rv);

        ArrayList<Pod_org> pod_org = new ArrayList<>();
        pod_org.add(new Pod_org("Vikash Organization",
                "https://images.yalwa.in/1023799707/Vikash-Organization_1.jpg",
                "https://www.vikash.org/support-to-disable-destitute/"));

        pod_org.add(new Pod_org("Diya India",
                "https://pbs.twimg.com/profile_images/883226017183092736/l-YLh8zs.jpg",
                "https://diyaindia.org/donate1.php"));

        pod_org.add(new Pod_org("Adarsh Charitable Trust",
                "https://adarshrehab.org/wp-content/uploads/2020/12/logo.jpg",
                "http://adarshrehab.org/campaigns/your-mite-is-our-might/donate/"));

        pod_org.add(new Pod_org("Vikas Disabled Charitable Trust",
                "data:image/png;base64,",
                "https://www.vdct.in/donateonline.php"));

        pod_org.add(new Pod_org("Samarthanam Trust for the Disabled",
                "https://upload.wikimedia.org/wikipedia/commons/0/08/Samarthanam_Trust_for_Disabled.png",
                "https://www.samarthanam.org/donate/"));

        pod_org.add(new Pod_org("Asha Bhavan Centre",
                "https://meramitra.in/wp-content/uploads//2020/07/IMG_20200714_162302-988x1024.jpg",
                "https://www.abcindia.org/donation/"));

        IndiaPod_rvAdapter adapter = new IndiaPod_rvAdapter(this);
        adapter.setPod_org(pod_org);

        pod_rv.setAdapter(adapter);
        pod_rv.setLayoutManager(new GridLayoutManager(this, 2));
    }
}